CREATE TABLE "Order" (idOrder number(10) GENERATED AS IDENTITY, deliveryDate date NOT NULL, orderDate date NOT NULL, CustomeridCustomer number(10) NOT NULL, PRIMARY KEY (idOrder));
CREATE TABLE ProductFamily (idProductFamily number(10) GENERATED AS IDENTITY, name varchar2(255) NOT NULL, PRIMARY KEY (idProductFamily));
CREATE TABLE BOM (idBOM number(10) GENERATED AS IDENTITY, ProductidProduct number(10) NOT NULL, PRIMARY KEY (idBOM));
CREATE TABLE RawMaterial (idRawMaterial number(10) GENERATED AS IDENTITY, Description varchar2(255) NOT NULL, PRIMARY KEY (idRawMaterial));
CREATE TABLE Component (idComponent number(10) GENERATED AS IDENTITY, CodeComponent varchar2(255) NOT NULL UNIQUE, Description varchar2(255) NOT NULL, PRIMARY KEY (idComponent));
CREATE TABLE CustomerType (idCustomerType number(10) GENERATED AS IDENTITY, name varchar2(255) NOT NULL, PRIMARY KEY (idCustomerType));
