CREATE TABLE WorkstationType (idWorkstationType number(10) GENERATED AS IDENTITY, workstationCode varchar2(255) NOT NULL UNIQUE, name varchar2(255) NOT NULL, PRIMARY KEY (idWorkstationType));
CREATE TABLE Customer (idCustomer number(10) GENERATED AS IDENTITY, name varchar2(255) NOT NULL, VATIN varchar2(255) NOT NULL, address varchar2(255) NOT NULL, zipCode varchar2(255) NOT NULL, town varchar2(255) NOT NULL, country varchar2(255) NOT NULL, email varchar2(255) NOT NULL, phoneNumber number(12) NOT NULL, CustomerTypeidCustomerType number(10) NOT NULL, PRIMARY KEY (idCustomer));
CREATE TABLE Product (idProduct number(10) GENERATED AS IDENTITY, productCode varchar2(255) NOT NULL UNIQUE, name varchar2(255) NOT NULL, description varchar2(255) NOT NULL, ProductFamilyidProductFamily number(10) NOT NULL, PRIMARY KEY (idProduct));
