CREATE TABLE BOM_Component (BOMidBOM number(10) NOT NULL, ComponentidComponent number(10) NOT NULL, quantity number(10) NOT NULL, ProductidProduct number(10) NOT NULL, PRIMARY KEY (BOMidBOM, ComponentidComponent));
